import OpenAI from "openai";

const openai = new OpenAI({ 
  apiKey: process.env.OPENAI_API_KEY || process.env.OPENAI_API_KEY_ENV_VAR || "default_key"
});

export interface ConversationInsights {
  // Score global de qualit√© de la conversation (0-100)
  conversationQualityScore: number;

  // Analyse du timing commercial
  salesTiming: {
    currentPhase: "d√©couverte" | "pr√©sentation" | "n√©gociation" | "closing" | "suivi";
    nextPhaseRecommendation: string;
    timeToClose: "imm√©diat" | "court" | "moyen" | "long";
    urgencyIndicators: string[];
  };

  // D√©tection des moments cl√©s
  keyMoments: Array<{
    moment: string;
    significance: "critique" | "important" | "notable";
    action: string;
  }>;

  // Analyse competitive
  competitiveAnalysis: {
    competitorsDetected: string[];
    competitiveAdvantages: string[];
    threatLevel: "low" | "medium" | "high";
    counterStrategies: string[];
  };

  // Score de maturit√© du prospect
  prospectMaturity: {
    decisionMakingStage: "reconnaissance" | "√©valuation" | "comparaison" | "d√©cision";
    readinessScore: number; // 0-100
    missingElements: string[];
  };

  // Pr√©dictions IA avanc√©es
  predictions: {
    closingProbability: number; // 0-100
    bestApproachVector: string;
    predictedObjections: Array<{
      objection: string;
      probability: number;
      preventiveStrategy: string;
    }>;
  };
}

export async function generateAdvancedInsights(conversationText: string): Promise<ConversationInsights> {
  try {
    const prompt = `Tu es le meilleur analyste commercial IA au monde, expert en neuro-science commerciale et psychologie comportementale. Analyse cette conversation commerciale avec la pr√©cision d'un super-ordinateur :

CONVERSATION :
${conversationText}

ANALYSE ULTRA-AVANC√âE REQUISE (JSON UNIQUEMENT) :

1. SCORE DE QUALIT√â CONVERSATION (0-100) bas√© sur l'efficacit√© commerciale
2. ANALYSE DE TIMING COMMERCIAL avec phase actuelle et recommandations de progression
3. MOMENTS CL√âS CRITIQUES d√©tect√©s dans la conversation 
4. ANALYSE COMPETITIVE avec d√©tection de concurrents et strat√©gies
5. MATURIT√â DU PROSPECT avec niveau de pr√©paration √† l'achat
6. PR√âDICTIONS IA avec probabilit√© de closing et strat√©gies optimales

STRUCTURE JSON OBLIGATOIRE :
{
  "conversationQualityScore": 85,
  "salesTiming": {
    "currentPhase": "d√©couverte|pr√©sentation|n√©gociation|closing|suivi",
    "nextPhaseRecommendation": "strat√©gie pour passer √† la phase suivante",
    "timeToClose": "imm√©diat|court|moyen|long",
    "urgencyIndicators": ["indicateur1", "indicateur2"]
  },
  "keyMoments": [
    {
      "moment": "moment cl√© d√©tect√©",
      "significance": "critique|important|notable",
      "action": "action recommand√©e"
    }
  ],
  "competitiveAnalysis": {
    "competitorsDetected": ["concurrent1", "concurrent2"],
    "competitiveAdvantages": ["avantage1", "avantage2"],
    "threatLevel": "low|medium|high",
    "counterStrategies": ["strat√©gie1", "strat√©gie2"]
  },
  "prospectMaturity": {
    "decisionMakingStage": "reconnaissance|√©valuation|comparaison|d√©cision",
    "readinessScore": 75,
    "missingElements": ["√©l√©ment1", "√©l√©ment2"]
  },
  "predictions": {
    "closingProbability": 65,
    "bestApproachVector": "meilleure approche recommand√©e",
    "predictedObjections": [
      {
        "objection": "objection pr√©dite",
        "probability": 70,
        "preventiveStrategy": "strat√©gie pr√©ventive"
      }
    ]
  }
}`;

    const response = await openai.chat.completions.create({
      model: "gpt-4o",
      messages: [
        {
          role: "system",
          content: "Tu es le meilleur analyste commercial IA au monde. G√©n√®re uniquement du JSON valide selon la structure exacte demand√©e."
        },
        {
          role: "user",
          content: prompt,
        },
      ],
      response_format: { type: "json_object" },
      temperature: 0.2,
      max_tokens: 3000,
    });

    const result = JSON.parse(response.choices[0].message.content || "{}");

    // Validation des champs requis
    const requiredFields = [
      'conversationQualityScore', 'salesTiming', 'keyMoments', 
      'competitiveAnalysis', 'prospectMaturity', 'predictions'
    ];

    for (const field of requiredFields) {
      if (!(field in result)) {
        throw new Error(`Champ requis manquant: ${field}`);
      }
    }

    return result as ConversationInsights;
  } catch (error) {
    console.error("Erreur lors de l'analyse avanc√©e:", error);
    throw new Error("√âchec de l'analyse avanc√©e: " + (error as Error).message);
  }
}

// Analyse de sentiment √©motionnel avanc√©e
export async function analyzeEmotionalJourney(conversationText: string): Promise<{
  emotionalTrajectory: Array<{
    phase: string;
    emotion: string;
    intensity: number;
    triggers: string[];
  }>;
  overallSentiment: number; // -100 to +100
  emotionalTriggers: string[];
  recommendedEmotionalApproach: string;
}> {
  try {
    const prompt = `Analyse le parcours √©motionnel de cette conversation commerciale :

${conversationText}

Trace la trajectoire √©motionnelle du prospect et identifie les d√©clencheurs √©motionnels.

JSON structure:
{
  "emotionalTrajectory": [
    {
      "phase": "d√©but de conversation",
      "emotion": "curiosit√©",
      "intensity": 7,
      "triggers": ["mention du probl√®me", "solution propos√©e"]
    }
  ],
  "overallSentiment": 65,
  "emotionalTriggers": ["trigger1", "trigger2"],
  "recommendedEmotionalApproach": "approche √©motionnelle recommand√©e"
}`;

    const response = await openai.chat.completions.create({
      model: "gpt-4o",
      messages: [
        { role: "system", content: "Tu es un expert en psychologie √©motionnelle commerciale." },
        { role: "user", content: prompt }
      ],
      response_format: { type: "json_object" },
      temperature: 0.3
    });

    return JSON.parse(response.choices[0].message.content || "{}");
  } catch (error) {
    console.error("Erreur analyse √©motionnelle:", error);
    throw error;
  }
}

server/db.ts.content:
import { Pool, neonConfig } from '@neondatabase/serverless';
import { drizzle } from 'drizzle-orm/neon-serverless';
import ws from "ws";
import * as schema from "@shared/schema";

// Configure Neon with WebSocket constructor and connection settings
neonConfig.webSocketConstructor = ws;
neonConfig.useSecureWebSocket = true;
neonConfig.pipelineConnect = false;

if (!process.env.DATABASE_URL) {
  throw new Error(
    "DATABASE_URL must be set. Did you forget to provision a database?",
  );
}

// Configure pool with proper connection settings
export const pool = new Pool({ 
  connectionString: process.env.DATABASE_URL,
  max: 10,
  idleTimeoutMillis: 30000,
  connectionTimeoutMillis: 5000,
});

export const db = drizzle({ client: pool, schema });

// Graceful shutdown
process.on('SIGINT', async () => {
  console.log('Closing database pool...');
  await pool.end();
  process.exit(0);
});

process.on('SIGTERM', async () => {
  console.log('Closing database pool...');
  await pool.end();
  process.exit(0);
});

server/index.ts.content:
import express, { type Request, Response, NextFunction } from "express";
import { registerRoutes } from "./routes";
import { setupVite, serveStatic, log } from "./vite";

const app = express();
app.use(express.json());
app.use(express.urlencoded({ extended: false }));

app.use((req, res, next) => {
  const start = Date.now();
  const path = req.path;
  let capturedJsonResponse: Record<string, any> | undefined = undefined;

  const originalResJson = res.json;
  res.json = function (bodyJson, ...args) {
    capturedJsonResponse = bodyJson;
    return originalResJson.apply(res, [bodyJson, ...args]);
  };

  res.on("finish", () => {
    const duration = Date.now() - start;
    if (path.startsWith("/api")) {
      let logLine = `${req.method} ${path} ${res.statusCode} in ${duration}ms`;
      if (capturedJsonResponse) {
        logLine += ` :: ${JSON.stringify(capturedJsonResponse)}`;
      }

      if (logLine.length > 80) {
        logLine = logLine.slice(0, 79) + "‚Ä¶";
      }

      log(logLine);
    }
  });

  next();
});

(async () => {
  const server = await registerRoutes(app);

  // Enhanced error handler
  app.use((err: any, _req: Request, res: Response, _next: NextFunction) => {
    console.error('Error handler caught:', err);

    const status = err.status || err.statusCode || 500;
    let message = err.message || "Internal Server Error";

    // Translate common errors to French and hide internal details
    if (status === 400) {
      message = 'Requ√™te invalide';
    } else if (status === 401) {
      message = 'Authentification requise';
    } else if (status === 403) {
      message = 'Acc√®s interdit';
    } else if (status === 404) {
      message = 'Ressource introuvable';
    } else if (status >= 500) {
      message = 'Erreur temporaire du serveur. Veuillez r√©essayer.';
      // Log detailed error but don't expose to client
      console.error('Server error details:', err.stack);
    }

    res.status(status).json({ 
      message,
      ...(process.env.NODE_ENV === 'development' && { error: err.message })
    });
  });

  // 404 handler for API routes
  app.use('/api/*', (_req: Request, res: Response) => {
    res.status(404).json({ message: 'Endpoint API introuvable' });
  });

  // importantly only setup vite in development and after
  // setting up all the other routes so the catch-all route
  // doesn't interfere with the other routes
  if (app.get("env") === "development") {
    await setupVite(app, server);
  } else {
    serveStatic(app);
  }

  // ALWAYS serve the app on the port specified in the environment variable PORT
  // Other ports are firewalled. Default to 5000 if not specified.
  // this serves both the API and the client.
  // It is the only port that is not firewalled.
  const port = parseInt(process.env.PORT || '5000', 10);
  server.listen({
    port,
    host: "0.0.0.0",
    reusePort: true,
  }, () => {
    log(`serving on port ${port}`);
  });
})();

server/objectStorage.ts.content:
import { Storage, File } from "@google-cloud/storage";
import { Response } from "express";
import { randomUUID } from "crypto";

const REPLIT_SIDECAR_ENDPOINT = "http://127.0.0.1:1106";

// The object storage client is used to interact with the object storage service.
export const objectStorageClient = new Storage({
  credentials: {
    audience: "replit",
    subject_token_type: "access_token",
    token_url: `${REPLIT_SIDECAR_ENDPOINT}/token`,
    type: "external_account",
    credential_source: {
      url: `${REPLIT_SIDECAR_ENDPOINT}/credential`,
      format: {
        type: "json",
        subject_token_field_name: "access_token",
      },
    },
    universe_domain: "googleapis.com",
  },
  projectId: "",
});

export class ObjectNotFoundError extends Error {
  constructor() {
    super("Object not found");
    this.name = "ObjectNotFoundError";
    Object.setPrototypeOf(this, ObjectNotFoundError.prototype);
  }
}

// The object storage service is used to interact with the object storage service.
export class ObjectStorageService {
  constructor() {}

  // Gets the public object search paths.
  getPublicObjectSearchPaths(): Array<string> {
    const pathsStr = process.env.PUBLIC_OBJECT_SEARCH_PATHS || "";
    const paths = Array.from(
      new Set(
        pathsStr
          .split(",")
          .map((path) => path.trim())
          .filter((path) => path.length > 0)
      )
    );
    if (paths.length === 0) {
      throw new Error(
        "PUBLIC_OBJECT_SEARCH_PATHS not set. Create a bucket in 'Object Storage' " +
          "tool and set PUBLIC_OBJECT_SEARCH_PATHS env var (comma-separated paths)."
      );
    }
    return paths;
  }

  // Gets the private object directory.
  getPrivateObjectDir(): string {
    const dir = process.env.PRIVATE_OBJECT_DIR || "";
    if (!dir) {
      throw new Error(
        "PRIVATE_OBJECT_DIR not set. Create a bucket in 'Object Storage' " +
          "tool and set PRIVATE_OBJECT_DIR env var."
      );
    }
    return dir;
  }

  // Search for a public object from the search paths.
  async searchPublicObject(filePath: string): Promise<File | null> {
    for (const searchPath of this.getPublicObjectSearchPaths()) {
      const fullPath = `${searchPath}/${filePath}`;

      // Full path format: /<bucket_name>/<object_name>
      const { bucketName, objectName } = parseObjectPath(fullPath);
      const bucket = objectStorageClient.bucket(bucketName);
      const file = bucket.file(objectName);

      // Check if file exists
      const [exists] = await file.exists();
      if (exists) {
        return file;
      }
    }

    return null;
  }

  // Downloads an object to the response.
  async downloadObject(file: File, res: Response, cacheTtlSec: number = 3600) {
    try {
      // Get file metadata
      const [metadata] = await file.getMetadata();

      // Set appropriate headers
      res.set({
        "Content-Type": metadata.contentType || "application/octet-stream",
        "Content-Length": metadata.size,
        "Cache-Control": `private, max-age=${cacheTtlSec}`,
      });

      // Stream the file to the response
      const stream = file.createReadStream();

      stream.on("error", (err) => {
        console.error("Stream error:", err);
        if (!res.headersSent) {
          res.status(500).json({ error: "Error streaming file" });
        }
      });

      stream.pipe(res);
    } catch (error) {
      console.error("Error downloading file:", error);
      if (!res.headersSent) {
        res.status(500).json({ error: "Error downloading file" });
      }
    }
  }

  // Gets the upload URL for an audio file
  async getAudioUploadURL(): Promise<string> {
    const privateObjectDir = this.getPrivateObjectDir();
    if (!privateObjectDir) {
      throw new Error(
        "PRIVATE_OBJECT_DIR not set. Create a bucket in 'Object Storage' " +
          "tool and set PRIVATE_OBJECT_DIR env var."
      );
    }

    const audioId = randomUUID();
    const fullPath = `${privateObjectDir}/audio/${audioId}`;

    const { bucketName, objectName } = parseObjectPath(fullPath);

    // Sign URL for PUT method with TTL
    return signObjectURL({
      bucketName,
      objectName,
      method: "PUT",
      ttlSec: 900, // 15 minutes
    });
  }

  // Gets the audio file from the object path.
  async getAudioFile(objectPath: string): Promise<File> {
    if (!objectPath.startsWith("/audio/")) {
      throw new ObjectNotFoundError();
    }

    const parts = objectPath.slice(1).split("/");
    if (parts.length < 2) {
      throw new ObjectNotFoundError();
    }

    const audioId = parts.slice(1).join("/");
    let audioDir = this.getPrivateObjectDir();
    if (!audioDir.endsWith("/")) {
      audioDir = `${audioDir}/`;
    }
    const audioObjectPath = `${audioDir}${objectPath.slice(1)}`;
    const { bucketName, objectName } = parseObjectPath(audioObjectPath);
    const bucket = objectStorageClient.bucket(bucketName);
    const audioFile = bucket.file(objectName);
    const [exists] = await audioFile.exists();
    if (!exists) {
      throw new ObjectNotFoundError();
    }
    return audioFile;
  }

  // Downloads an audio file to a local temporary path for processing
  async downloadAudioToTemp(audioFile: File): Promise<string> {
    const fs = require('fs');
    const tempPath = `/tmp/audio_${randomUUID()}.tmp`;

    return new Promise((resolve, reject) => {
      const writeStream = fs.createWriteStream(tempPath);
      const readStream = audioFile.createReadStream();

      readStream.on('error', reject);
      writeStream.on('error', reject);
      writeStream.on('finish', () => resolve(tempPath));

      readStream.pipe(writeStream);
    });
  }

  normalizeAudioPath(rawPath: string): string {
    if (!rawPath.startsWith("https://storage.googleapis.com/")) {
      return rawPath;
    }

    // Extract the path from the URL by removing query parameters and domain
    const url = new URL(rawPath);
    const rawObjectPath = url.pathname;

    let audioDir = this.getPrivateObjectDir();
    if (!audioDir.endsWith("/")) {
      audioDir = `${audioDir}/`;
    }

    if (!rawObjectPath.startsWith(audioDir)) {
      return rawObjectPath;
    }

    // Extract the audio ID from the path
    const audioId = rawObjectPath.slice(audioDir.length);
    return `/audio/${audioId}`;
  }
}

function parseObjectPath(path: string): {
  bucketName: string;
  objectName: string;
} {
  if (!path.startsWith("/")) {
    path = `/${path}`;
  }
  const pathParts = path.split("/");
  if (pathParts.length < 3) {
    throw new Error("Invalid path: must contain at least a bucket name");
  }

  const bucketName = pathParts[1];
  const objectName = pathParts.slice(2).join("/");

  return {
    bucketName,
    objectName,
  };
}

async function signObjectURL({
  bucketName,
  objectName,
  method,
  ttlSec,
}: {
  bucketName: string;
  objectName: string;
  method: "GET" | "PUT" | "DELETE" | "HEAD";
  ttlSec: number;
}): Promise<string> {
  const request = {
    bucket_name: bucketName,
    object_name: objectName,
    method,
    expires_at: new Date(Date.now() + ttlSec * 1000).toISOString(),
  };
  const response = await fetch(
    `${REPLIT_SIDECAR_ENDPOINT}/object-storage/signed-object-url`,
    {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(request),
    }
  );
  if (!response.ok) {
    throw new Error(
      `Failed to sign object URL, errorcode: ${response.status}, ` +
        `make sure you're running on Replit`
    );
  }

  const { signed_url: signedURL } = await response.json();
  return signedURL;
}

server/openai.ts.content:
import OpenAI from "openai";
import fs from "fs";
import path from "path";
import { AdvancedAudioProcessor } from "./audioProcessor";
import { createHash } from "crypto";

// the newest OpenAI model is "gpt-4o" which was released May 13, 2024. do not change this unless explicitly requested by the user
if (!process.env.OPENAI_API_KEY) {
  throw new Error("OPENAI_API_KEY environment variable is required");
}

const openai = new OpenAI({ 
  apiKey: process.env.OPENAI_API_KEY,
  timeout: 60000, // 60 seconds timeout for robust processing
  maxRetries: 3, // Auto-retry on failure
});

// Advanced audio processing utilities
class AudioProcessor {
  private static readonly SUPPORTED_FORMATS = [
    '.mp3', '.mp4', '.mpeg', '.mpga', '.m4a', '.wav', '.webm', '.flac', '.ogg', '.opus'
  ];

  private static readonly MAX_FILE_SIZE = 25 * 1024 * 1024; // 25MB (Whisper limit)
  private static readonly MIN_FILE_SIZE = 1024; // 1KB minimum

  static validateAudioFile(filePath: string): void {
    if (!fs.existsSync(filePath)) {
      throw new Error("Fichier audio introuvable");
    }

    const stats = fs.statSync(filePath);
    const fileSize = stats.size;
    const fileExt = path.extname(filePath).toLowerCase();

    if (fileSize < this.MIN_FILE_SIZE) {
      throw new Error("Fichier audio trop petit (minimum 1KB)");
    }

    if (fileSize > this.MAX_FILE_SIZE) {
      throw new Error(`Fichier audio trop volumineux (maximum 25MB, re√ßu ${Math.round(fileSize / 1024 / 1024)}MB)`);
    }

    if (!this.SUPPORTED_FORMATS.includes(fileExt)) {
      throw new Error(`Format audio non support√©: ${fileExt}. Formats accept√©s: ${this.SUPPORTED_FORMATS.join(', ')}`);
    }
  }

  static generateFileHash(filePath: string): string {
    const fileBuffer = fs.readFileSync(filePath);
    return createHash('sha256').update(fileBuffer).digest('hex');
  }

  static estimateDuration(fileSize: number): number {
    // Rough estimation: 1MB ‚âà 1 minute for average quality audio
    return Math.max(1, Math.round(fileSize / (1024 * 1024)));
  }
}

// Advanced text preprocessing for optimal analysis
class TextProcessor {
  private static readonly MIN_TEXT_LENGTH = 10;
  private static readonly MAX_TEXT_LENGTH = 100000; // Increased limit
  private static readonly CONVERSATION_PATTERNS = [
    /\b(vendeur|commercial|client|prospect|acheteur|repr√©sentant)\s*:/gi,
    /\b(moi|vous|nous|je|tu|il|elle)\s*:/gi,
    /\b\d{1,2}:\d{2}\s*(am|pm)?\b/gi, // Timestamps
  ];

  static preprocessConversation(text: string): {
    cleanedText: string;
    metadata: {
      originalLength: number;
      cleanedLength: number;
      hasTimestamps: boolean;
      hasSpeakerLabels: boolean;
      estimatedParticipants: number;
      language: 'fr' | 'en' | 'mixed';
    };
  } {
    if (!text || typeof text !== 'string') {
      throw new Error("Texte de conversation invalide");
    }

    const originalLength = text.length;

    if (originalLength < this.MIN_TEXT_LENGTH) {
      throw new Error(`Texte trop court (minimum ${this.MIN_TEXT_LENGTH} caract√®res)`);
    }

    if (originalLength > this.MAX_TEXT_LENGTH) {
      throw new Error(`Texte trop long (maximum ${this.MAX_TEXT_LENGTH} caract√®res, re√ßu ${originalLength})`);
    }

    // Clean and normalize text
    let cleanedText = text
      .replace(/\r\n/g, '\n') // Normalize line endings
      .replace(/\s+/g, ' ') // Normalize whitespace
      .trim();

    // Detect conversation patterns
    const hasTimestamps = /\b\d{1,2}:\d{2}/.test(cleanedText);
    const hasSpeakerLabels = /:/.test(cleanedText) && /\b(vendeur|commercial|client|prospect|moi|vous)\s*:/i.test(cleanedText);

    // Estimate participants
    const speakerMatches = cleanedText.match(/\b\w+\s*:/g) || [];
    const uniqueSpeakers = new Set(speakerMatches.map(s => s.toLowerCase()));
    const estimatedParticipants = Math.max(2, uniqueSpeakers.size);

    // Detect language
    const frenchWords = (cleanedText.match(/\b(le|la|les|un|une|des|et|de|du|dans|sur|avec|pour|par|ce|cette|qui|que|dont|o√π|oui|non|tr√®s|mais|comme|tout|bien|plus|encore|aussi|peut|√™tre|avoir|faire|dire|aller|voir|savoir|nous|vous|ils|elles)\b/gi) || []).length;
    const englishWords = (cleanedText.match(/\b(the|and|or|but|in|on|at|to|for|of|with|by|from|this|that|which|who|what|where|when|why|how|yes|no|very|more|also|can|will|would|could|should|have|has|had|do|does|did|go|come|see|know|think|say|get|make|take|give|find|use|work|try|ask|tell|show|play|run|move|live|feel|seem|become|leave|turn|start|stop|help|talk|walk|look|want|need|like|love|hate|hope|wish|believe|understand|remember|forget|learn|teach|study|read|write|listen|hear|speak|call|answer|open|close|buy|sell|pay|cost|spend|save|win|lose|choose|decide|agree|disagree|accept|refuse|allow|prevent|protect|attack|defend|fight|argue|discuss|explain|describe|compare|contrast|analyze|solve|create|build|destroy|repair|clean|cook|eat|drink|sleep|wake|dream|smile|laugh|cry|sing|dance|drive|ride|fly|swim|jump|run|walk|sit|stand|lie|fall|rise|grow|change|improve|increase|decrease|begin|end|continue|stop|start|finish|complete|succeed|fail|try|attempt|achieve|reach|arrive|leave|return|stay|remain|exist|happen|occur|appear|disappear|seem|look|sound|feel|taste|smell|touch|hold|carry|bring|take|put|place|keep|store|find|lose|search|discover|invent|create)\b/gi) || []).length;

    let language: 'fr' | 'en' | 'mixed' = 'fr';
    if (englishWords > frenchWords * 1.5) {
      language = 'en';
    } else if (englishWords > frenchWords * 0.3) {
      language = 'mixed';
    }

    return {
      cleanedText,
      metadata: {
        originalLength,
        cleanedLength: cleanedText.length,
        hasTimestamps,
        hasSpeakerLabels,
        estimatedParticipants,
        language,
      }
    };
  }
}

