import { useState, useEffect } from "react";
import { useMutation, useQuery } from "@tanstack/react-query";
import { Link, useLocation } from "wouter";
import { ArrowLeft, FileAudio, Mic, Brain, Zap, MessageSquare, TrendingUp, AlertTriangle, CheckCircle2, FlipHorizontal2 } from "lucide-react";
import Footer from "@/components/Footer";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Separator } from "@/components/ui/separator";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { AudioUploader } from "@/components/AudioUploader";
import { DirectAudioUploader } from "@/components/DirectAudioUploader";
import { RevolutionaryAudioAnalyzer } from "@/components/RevolutionaryAudioAnalyzer";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import { Analysis, User } from "@shared/schema";
import { useAuth } from "@/hooks/useAuth";
import { isUnauthorizedError } from "@/lib/authUtils";

interface AudioMetadata {
  duration: number;
  fileSize: number;
  fileName: string;
  audioPath: string;
}

export default function AudioAnalysis() {
  const { toast } = useToast();
  const [, setLocation] = useLocation();
  const { user, isAuthenticated, isLoading: userLoading } = useAuth();
  const [transcription, setTranscription] = useState("");
  const [audioMetadata, setAudioMetadata] = useState<AudioMetadata | null>(null);
  const [analysisTitle, setAnalysisTitle] = useState("");
  const [isAnalyzing, setIsAnalyzing] = useState(false);
  const [isTranscribing, setIsTranscribing] = useState(false);
  const [analysisResult, setAnalysisResult] = useState<Analysis & { audioInsights?: any } | null>(null);
  const [useDirectUpload, setUseDirectUpload] = useState(true); // Default to revolutionary system

  // Redirect to login if not authenticated
  useEffect(() => {
    if (!userLoading && !isAuthenticated) {
      toast({
        title: "Non autoris√©",
        description: "Vous √™tes d√©connect√©. Reconnexion en cours...",
        variant: "destructive",
      });
      setTimeout(() => {
        setLocation("/");
      }, 500);
      return;
    }
  }, [isAuthenticated, userLoading, toast]);

  // Audio analysis mutation
  const analyzeMutation = useMutation({
    mutationFn: async (data: {
      transcriptionText: string;
      title: string;
      audioPath: string;
      fileName: string;
      duration: number;
      fileSize: number;
    }) => {
      const response = await apiRequest("POST", "/api/analyze-audio", data);
      return await response.json();
    },
    onSuccess: (data) => {
      setAnalysisResult(data);
      setIsAnalyzing(false);
      toast({
        title: "Analyse termin√©e",
        description: "L'analyse IA de votre conversation audio est maintenant disponible.",
      });
    },
    onError: (error) => {
      setIsAnalyzing(false);
      toast({
        title: "Erreur d'analyse",
        description: error.message || "Impossible d'analyser la conversation audio.",
        variant: "destructive",
      });
    },
  });

  // Transcription mutation
  const transcribeMutation = useMutation({
    mutationFn: async (data: {
      audioURL: string;
      fileName: string;
      fileSize: number;
    }) => {
      const response = await apiRequest("POST", "/api/audio/transcribe", data);
      return await response.json();
    },
    onSuccess: (data) => {
      setTranscription(data.transcription);
      setAudioMetadata({
        duration: data.duration,
        fileSize: data.fileSize || 0,
        fileName: data.fileName,
        audioPath: data.audioPath,
      });
      setIsTranscribing(false);
      toast({
        title: "Transcription termin√©e",
        description: "Votre fichier audio a √©t√© transcrit avec succ√®s.",
      });
    },
    onError: (error) => {
      setIsTranscribing(false);
      if (isUnauthorizedError(error)) {
        toast({
          title: "Non autoris√©",
          description: "Vous √™tes d√©connect√©. Reconnexion en cours...",
          variant: "destructive",
        });
        setTimeout(() => {
          window.location.href = "/api/login";
        }, 500);
        return;
      }
      toast({
        title: "Erreur de transcription",
        description: error.message || "Impossible de transcrire le fichier audio.",
        variant: "destructive",
      });
    },
  });

  const handleAudioUploadComplete = (audioUrl: string, fileName: string, fileSize: number) => {
    setIsTranscribing(true);
    transcribeMutation.mutate({
      audioURL: audioUrl,
      fileName: fileName,
      fileSize: fileSize,
    });
  };

  const handleAnalyze = () => {
    if (!transcription || !audioMetadata) {
      toast({
        title: "Transcription manquante",
        description: "Veuillez d'abord uploader et transcrire un fichier audio.",
        variant: "destructive",
      });
      return;
    }

    setIsAnalyzing(true);
    analyzeMutation.mutate({
      transcriptionText: transcription,
      title: analysisTitle || "Analyse audio",
      audioPath: audioMetadata.audioPath,
      fileName: audioMetadata.fileName,
      duration: audioMetadata.duration,
      fileSize: audioMetadata.fileSize,
    });
  };

  const formatDuration = (seconds: number) => {
    const mins = Math.floor(seconds / 60);
    const secs = Math.floor(seconds % 60);
    return `${mins}:${secs.toString().padStart(2, '0')}`;
  };

  const formatFileSize = (bytes: number) => {
    const mb = bytes / (1024 * 1024);
    return `${mb.toFixed(1)} MB`;
  };

  const getInterestLevelColor = (level: string) => {
    switch (level) {
      case "hot": return "bg-red-100 text-red-800 dark:bg-red-900/20 dark:text-red-300";
      case "warm": return "bg-orange-100 text-orange-800 dark:bg-orange-900/20 dark:text-orange-300";
      case "cold": return "bg-blue-100 text-blue-800 dark:bg-blue-900/20 dark:text-blue-300";
      default: return "bg-gray-100 text-gray-800 dark:bg-gray-900/20 dark:text-gray-300";
    }
  };

  return (
    <div className="container mx-auto py-6 space-y-6">
      {/* Header */}
      <div className="flex items-center space-x-4">
        <Button 
          variant="ghost" 
          size="sm"
          onClick={() => {
            console.log("Navigation vers dashboard...");
            window.location.href = "/";
          }}
        >
          <ArrowLeft className="w-4 h-4 mr-2" />
          Retour au tableau de bord
        </Button>
        <div>
          <h1 className="text-3xl font-bold flex items-center space-x-2">
            <FileAudio className="w-8 h-8 text-blue-500" />
            <span>Analyse audio IA</span>
          </h1>
          <p className="text-muted-foreground">
            Uploadez et analysez vos appels commerciaux avec l'IA Whisper et GPT-4o
          </p>
        </div>
      </div>

      {/* Usage Info */}
      {user && (
        <Card className="bg-blue-50 dark:bg-blue-950/20 border-blue-200 dark:border-blue-800">
          <CardContent className="pt-6">
            <div className="flex items-center justify-between">
              <div className="flex items-center space-x-3">
                <Zap className="w-5 h-5 text-blue-600" />
                <div>
                  <p className="font-medium text-blue-800 dark:text-blue-200">
                    {user.isPremium ? "Premium - Analyses illimit√©es" : `Plan gratuit - ${user.monthlyAnalysesUsed || 0}/3 analyses utilis√©es ce mois`}
                  </p>
                  <p className="text-sm text-blue-600 dark:text-blue-300">
                    {user.isPremium ? "Transcription et analyse audio avanc√©e incluses" : "Upgrade vers Premium pour des analyses illimit√©es"}
                  </p>
                </div>
              </div>
              {!user.isPremium && (user.monthlyAnalysesUsed || 0) >= 3 && (
                <Link href="/subscribe">
                  <Button size="sm">
                    Upgrade vers Premium
                  </Button>
                </Link>
              )}
            </div>
          </CardContent>
        </Card>
      )}

      <div className="grid grid-cols-1 xl:grid-cols-2 gap-6">
        {/* Left Column - Upload and Configuration */}
        <div className="space-y-6">
          {/* Revolutionary Audio Upload */}
          <RevolutionaryAudioAnalyzer 
            onAnalysisComplete={(analysis) => {
              setAnalysisResult(analysis);
              toast({
                title: "Analyse R√©volutionnaire Termin√©e",
                description: "Votre analyse compl√®te est maintenant disponible avec tous les insights avanc√©s.",
              });
            }}
          />

          {/* Legacy Audio Upload (hidden) */}
          {false && (
            <Card>
            <CardHeader>
              <CardTitle className="flex items-center space-x-2">
                <Mic className="w-5 h-5" />
                <span>Upload de fichier audio</span>
              </CardTitle>
              <CardDescription>
                Uploadez un enregistrement d'appel commercial pour transcription et analyse IA
              </CardDescription>
            </CardHeader>
            <CardContent className="space-y-4">
              <AudioUploader
                onComplete={handleAudioUploadComplete}
                disabled={isTranscribing || isAnalyzing}
              >
                <div className="flex items-center space-x-2">
                  <FileAudio className="w-4 h-4" />
                  <span>
                    {isTranscribing ? "Transcription en cours..." : "S√©lectionner fichier audio"}
                  </span>
                </div>
              </AudioUploader>

              {isTranscribing && (
                <div className="bg-blue-50 dark:bg-blue-950/20 p-4 rounded-lg">
                  <div className="flex items-center space-x-2">
                    <Mic className="w-4 h-4 text-blue-600 animate-pulse" />
                    <span className="text-sm font-medium text-blue-800 dark:text-blue-200">
                      Transcription avec Whisper AI en cours...
                    </span>
                  </div>
                </div>
              )}

              <div className="mt-4 text-sm text-muted-foreground">
                <p>Formats support√©s: MP3, WAV, M4A, AAC, FLAC, OGG</p>
                <p>Taille max: 50MB</p>
              </div>
            </CardContent>
          </Card>
          )}

          {/* Analysis Configuration (hidden) */}
          {false && transcription && (
            <Card>
              <CardHeader>
                <CardTitle className="flex items-center space-x-2">
                  <Brain className="w-5 h-5" />
                  <span>Configuration de l'analyse</span>
                </CardTitle>
              </CardHeader>
              <CardContent className="space-y-4">
                <div>
                  <Label htmlFor="title">Titre de l'analyse</Label>
                  <Input
                    id="title"
                    value={analysisTitle}
                    onChange={(e) => setAnalysisTitle(e.target.value)}
                    placeholder=""
                    className="mt-1"
                  />
                </div>

                {audioMetadata && (
                  <div className="bg-muted/50 p-4 rounded-lg">
                    <h4 className="font-medium mb-2">Informations audio</h4>
                    <div className="grid grid-cols-2 gap-4 text-sm">
                      <div>
                        <p className="text-muted-foreground">Fichier</p>
                        <p className="font-medium">{audioMetadata.fileName}</p>
                      </div>
                      <div>
                        <p className="text-muted-foreground">Dur√©e</p>
                        <p className="font-medium">{formatDuration(audioMetadata.duration)}</p>
                      </div>
                      <div>
                        <p className="text-muted-foreground">Taille</p>
                        <p className="font-medium">{formatFileSize(audioMetadata.fileSize)}</p>
                      </div>
                      <div>
                        <p className="text-muted-foreground">Statut</p>
                        <Badge className="bg-green-100 text-green-800">
                          <CheckCircle2 className="w-3 h-3 mr-1" />
                          Transcrit
                        </Badge>
                      </div>
                    </div>
                  </div>
                )}

                <Button
                  onClick={handleAnalyze}
                  disabled={isAnalyzing || isTranscribing || !transcription || (user && !user.isPremium && (user.monthlyAnalysesUsed || 0) >= 3)}
                  className="w-full"
                  size="lg"
                >
                  {isAnalyzing ? (
                    <>
                      <Brain className="w-4 h-4 mr-2 animate-pulse" />
                      Analyse en cours...
                    </>
                  ) : (
                    <>
                      <Brain className="w-4 h-4 mr-2" />
                      Analyser la conversation
                    </>
                  )}
                </Button>
              </CardContent>
            </Card>
          )}
        </div>

        {/* Right Column - Transcription and Results */}
        <div className="space-y-6">
          {/* Transcription Preview */}
          {transcription && (
            <Card>
              <CardHeader>
                <CardTitle className="flex items-center space-x-2">
                  <MessageSquare className="w-5 h-5" />
                  <span>Transcription</span>
                </CardTitle>
                <CardDescription>
                  Texte transcrit de votre fichier audio
                </CardDescription>
              </CardHeader>
              <CardContent>
                <div className="bg-muted/50 p-4 rounded-lg max-h-60 overflow-y-auto">
                  <p className="text-sm whitespace-pre-wrap">{transcription}</p>
                </div>
              </CardContent>
            </Card>
          )}

          {/* Analysis Results */}
