import type { User } from "@shared/schema";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import { isUnauthorizedError } from "@/lib/authUtils";
import Onboarding from "@/components/onboarding";
import Footer from "@/components/Footer";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Textarea } from "@/components/ui/textarea";
import { Badge } from "@/components/ui/badge";
import { Progress } from "@/components/ui/progress";
import { Skeleton } from "@/components/ui/skeleton";
import { Dialog, DialogContent, DialogDescription, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { 
  FlipHorizontal2, 
  Sparkles, 
  Copy, 
  Clock, 
  TrendingUp, 
  History, 
  Crown, 
  Lightbulb, 
  Target, 
  User as UserIcon, 
  CheckCircle, 
  AlertTriangle, 
  Brain, 
  Mail, 
  Shuffle, 
  Shield, 
  ArrowRight,
  Save,
  Settings,
  Download,
  FileAudio,
  Mic
} from "lucide-react";
import { DropdownMenu, DropdownMenuContent, DropdownMenuItem, DropdownMenuLabel, DropdownMenuSeparator, DropdownMenuTrigger } from "@/components/ui/dropdown-menu";

interface Analysis {
  id: string;
  userId: string;
  title: string;
  inputText: string;
  interestLevel: "hot" | "warm" | "cold";
  interestJustification: string;
  confidenceScore?: number;
  personalityProfile?: {
    type: "analytical" | "driver" | "expressive" | "amiable";
    traits: string[];
    communicationStyle: string;
  };
  emotionalState?: {
    primary: "enthousiaste" | "prudent" | "frustr√©" | "neutre" | "excit√©";
    intensity: number;
    indicators: string[];
  };
  objections: Array<{
    type: string;
    intensity: "high" | "medium" | "low";
    description: string;
    responseStrategy?: string;
    probability?: number;
  }>;
  buyingSignals?: Array<{
    signal: string;
    strength: "strong" | "medium" | "weak";
    description: string;
  }>;
  nextSteps?: Array<{
    action: string;
    priority: "high" | "medium" | "low";
    timeframe: string;
    reasoning: string;
  }>;
  strategicAdvice: string;
  talkingPoints?: string[];
  followUpSubject: string;
  followUpMessage: string;
  alternativeApproaches?: Array<{
    approach: string;
    when: string;
    message: string;
  }>;
  riskFactors?: Array<{
    risk: string;
    impact: "high" | "medium" | "low";
    mitigation: string;
  }>;
  advancedInsights?: {
    conversationQualityScore: number;
    salesTiming: {
      currentPhase: string;
      nextPhaseRecommendation: string;
      timeToClose: string;
      urgencyIndicators: string[];
    };
    keyMoments: Array<{
      moment: string;
      significance: string;
      action: string;
    }>;
    competitiveAnalysis: {
      competitorsDetected: string[];
      competitiveAdvantages: string[];
      threatLevel: string;
      counterStrategies: string[];
    };
    prospectMaturity: {
      decisionMakingStage: string;
      readinessScore: number;
      missingElements: string[];
    };
    predictions: {
      closingProbability: number;
      bestApproachVector: string;
      predictedObjections: Array<{
        objection: string;
        probability: number;
        preventiveStrategy: string;
      }>;
    };
  };
  emotionalAnalysis?: {
    emotionalTrajectory: Array<{
      phase: string;
      emotion: string;
      intensity: number;
      triggers: string[];
    }>;
    overallSentiment: number;
    emotionalTriggers: string[];
    recommendedEmotionalApproach: string;
  };
  createdAt: string;
}

export default function Dashboard() {
  const { user, isLoading: userLoading } = useAuth();
  const { toast } = useToast();
  const [analysisTitle, setAnalysisTitle] = useState("");
  const [conversationText, setConversationText] = useState(``);
  const [currentAnalysis, setCurrentAnalysis] = useState<Analysis | null>(null);
  const [showHistory, setShowHistory] = useState(false);
  const [showPricing, setShowPricing] = useState(false); 
  const [showOnboarding, setShowOnboarding] = useState(false);

  // Logout mutation
  const logoutMutation = useMutation({
    mutationFn: () => apiRequest("POST", "/api/auth/logout"),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/auth/user"] });
      toast({
        title: "D√©connexion r√©ussie", 
        description: "Vous avez √©t√© d√©connect√© avec succ√®s",
      });
      // Redirection vers la page d'accueil apr√®s d√©connexion
      setTimeout(() => {
        window.location.href = "/";
      }, 1000);
    },
    onError: (error) => {
      console.error("Logout error:", error);
      toast({
        title: "Erreur de d√©connexion",
        description: "Une erreur s'est produite lors de la d√©connexion",
        variant: "destructive",
      });
    },
  });

  // Redirect to login if not authenticated
  useEffect(() => {
    if (!userLoading && !user) {
      toast({
        title: "Non autoris√©",
        description: "Vous √™tes d√©connect√©. Reconnexion en cours...",
        variant: "destructive",
      });
      setTimeout(() => {
        window.location.href = "/auth";
      }, 500);
      return;
    }
  }, [user, userLoading, toast]);

  // Show onboarding for new users
  useEffect(() => {
    if (user && (user.monthlyAnalysesUsed || 0) === 0 && !currentAnalysis) {
      const hasSeenOnboarding = localStorage.getItem('leadmirror-onboarding-seen');
      if (!hasSeenOnboarding) {
        setShowOnboarding(true);
      }
    }
  }, [user, currentAnalysis]);

  // Fetch user analyses
  const { data: analyses = [], isLoading: analysesLoading } = useQuery<Analysis[]>({
    queryKey: ["/api/analyses"],
    enabled: !!user?.isPremium,
    retry: false,
  });

  // Analysis mutation
  const analyzeMutation = useMutation({
    mutationFn: async (data: { text: string; title: string }) => {
      const response = await apiRequest("POST", "/api/analyze", { 
        conversationText: data.text, 
        title: data.title 
      });
      return await response.json();
    },
    onSuccess: (data) => {
      setCurrentAnalysis(data);
      queryClient.invalidateQueries({ queryKey: ["/api/auth/user"] });
      queryClient.invalidateQueries({ queryKey: ["/api/analyses"] });
      toast({
        title: "Analyse termin√©e",
        description: "Votre conversation a √©t√© analys√©e avec succ√®s.",
      });
    },
    onError: (error) => {
      if (isUnauthorizedError(error)) {
        toast({
          title: "Non autoris√©",
          description: "Vous √™tes d√©connect√©. Reconnexion en cours...",
          variant: "destructive",
        });
        setTimeout(() => {
          window.location.href = "/api/auth/google";
        }, 500);
        return;
      }

      const errorMessage = error.message;
      if (errorMessage.includes("limit reached")) {
        setShowPricing(true);
        toast({
          title: "Limite d'analyses atteinte",
          description: "Passez au premium pour des analyses illimit√©es.",
          variant: "destructive",
        });
      } else {
        toast({
          title: "√âchec de l'analyse",
          description: errorMessage,
          variant: "destructive",
        });
      }
    },
  });

  const handleAnalyze = (e: React.FormEvent) => {
    e.preventDefault();
    if (!conversationText.trim()) {
      toast({
        title: "Saisie manquante",
        description: "Veuillez saisir une conversation √† analyser.",
        variant: "destructive",
      });
      return;
    }
    analyzeMutation.mutate({ 
      text: conversationText.trim(), 
      title: analysisTitle.trim() || "Analyse sans titre" 
    });
  };

  const copyToClipboard = async (text: string) => {
    try {
      await navigator.clipboard.writeText(text);
      toast({
        title: "Copi√© !",
        description: "Texte copi√© dans le presse-papiers.",
      });
    } catch (error) {
      toast({
        title: "√âchec de la copie",
        description: "Impossible de copier le texte dans le presse-papiers.",
        variant: "destructive",
      });
    }
  };

  const getInterestColor = (level: string) => {
    switch (level) {
      case "hot": return "hot";
      case "warm": return "warm";
      case "cold": return "cold";
      default: return "default";
    }
  };

  const getObjectionColor = (intensity: string) => {
    switch (intensity) {
      case "high": return "bg-red-500";
      case "medium": return "bg-orange-500";
      case "low": return "bg-yellow-500";
      default: return "bg-gray-500";
    }
  };

  if (userLoading) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="animate-spin w-8 h-8 border-4 border-primary border-t-transparent rounded-full" />
      </div>
    );
  }

  if (!user) {
    return null;
  }

  const usagePercentage = user.isPremium ? 100 : ((user.monthlyAnalysesUsed || 0) / 3) * 100;

  return (
    <div className="min-h-screen bg-background">
      {/* Header */}
      <header className="bg-background border-b border-border sticky top-0 z-40">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex justify-between items-center h-16">
            <div className="flex items-center space-x-2">
              <div className="w-8 h-8 bg-primary rounded-lg flex items-center justify-center">
                <FlipHorizontal2 className="w-4 h-4 text-primary-foreground" />
              </div>
              <h1 className="text-xl font-bold text-foreground">LeadMirror</h1>
            </div>

            <div className="flex items-center space-x-4">
              <div className="flex items-center space-x-2">
                <Button 
                  variant="ghost" 
                  size="sm"
                  onClick={() => window.location.href = "/analytics"}
                  className="text-muted-foreground hover:text-foreground"
                >
                  <Target className="w-4 h-4 mr-1" />
                  Analytics
                </Button>
                <Button 
                  variant="ghost" 
                  size="sm"
                  onClick={() => window.location.href = "/integrations"}
                  className="text-muted-foreground hover:text-foreground"
                >
                  <Settings className="w-4 h-4 mr-1" />
                  Int√©grations
                </Button>
                <span className="text-sm text-muted-foreground">
                  Analyses utilis√©es : <span className="font-medium">{user.monthlyAnalysesUsed || 0}</span>
                  {!user.isPremium && "/3"}
                </span>
                {!user.isPremium && (
                  <Button 
                    size="sm" 
                    className="bg-accent hover:bg-accent/90"
                    onClick={() => setShowPricing(true)}
                  >
                    <Crown className="w-4 h-4 mr-1" />
                    Mettre √† niveau
                  </Button>
                )}
              </div>
              <div className="flex items-center space-x-2">
                <DropdownMenu>
                  <DropdownMenuTrigger asChild>
                    <Button variant="ghost" className="flex items-center space-x-2 hover:bg-muted">
                      <div className="w-8 h-8 bg-muted rounded-full flex items-center justify-center">
                        {user.profileImageUrl ? (
                          <img 
                            src={user.profileImageUrl} 
                            alt="Profile" 
                            className="w-8 h-8 rounded-full object-cover"
                          />
                        ) : (
                          <span className="text-sm font-medium">
                            {(user.firstName?.[0] || user.email?.[0] || "U").toUpperCase()}
                          </span>
                        )}
                      </div>
                      <span className="text-sm font-medium">
                        {user.firstName || user.email}
                      </span>
                      {user.isPremium && (
                        <Crown className="w-4 h-4 text-yellow-500" />
                      )}
                    </Button>
                  </DropdownMenuTrigger>
                  <DropdownMenuContent align="end" className="w-56">
                    <DropdownMenuLabel>Mon compte</DropdownMenuLabel>
                    <DropdownMenuSeparator />
                    <DropdownMenuItem onClick={() => window.location.href = "/profile"}>
                      <UserIcon className="mr-2 h-4 w-4" />
                      <span>Profil et abonnement</span>
                    </DropdownMenuItem>
                    <DropdownMenuItem onClick={() => window.location.href = "/analytics"}>
                      <Target className="mr-2 h-4 w-4" />
                      <span>Analytics</span>
                    </DropdownMenuItem>
                    <DropdownMenuItem onClick={() => window.location.href = "/integrations"}>
                      <Settings className="mr-2 h-4 w-4" />
                      <span>Int√©grations</span>
                    </DropdownMenuItem>
                    <DropdownMenuItem onClick={() => window.location.href = "/security"}>
                      <Shield className="mr-2 h-4 w-4" />
                      <span>S√©curit√©</span>
                    </DropdownMenuItem>
                    <DropdownMenuSeparator />
                    {!user.isPremium && (
                      <>
                        <DropdownMenuItem onClick={() => setShowPricing(true)}>
                          <Crown className="mr-2 h-4 w-4 text-yellow-500" />
                          <span>Passer au Premium</span>
                        </DropdownMenuItem>
                        <DropdownMenuSeparator />
                      </>
                    )}
                    <DropdownMenuItem onClick={() => logoutMutation.mutate()}>
                      <ArrowRight className="mr-2 h-4 w-4" />
                      <span>Se d√©connecter</span>
                    </DropdownMenuItem>
                  </DropdownMenuContent>
                </DropdownMenu>
              </div>
            </div>
          </div>
        </div>
      </header>

      {/* Main Content */}
      <main className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        <div className="mb-8">
          <h2 className="text-3xl font-bold text-foreground mb-2">
            Analysez vos conversations commerciales
          </h2>
          <p className="text-muted-foreground">
            Collez votre email ou r√©sum√© d'appel pour obtenir des insights IA et des messages de relance parfaits.
          </p>
        </div>

        <div className="grid lg:grid-cols-3 gap-8">
          {/* Analysis Input */}
          <div className="lg:col-span-2">
            <Card className="mb-8">
              <CardHeader>
                <CardTitle className="flex items-center space-x-2">
                  <Sparkles className="w-5 h-5" />
                  <span>Analyse de conversation</span>
                </CardTitle>
                <CardDescription>
                  Analysez vos conversations texte ou uploadez des fichiers audio d'appels commerciaux
                </CardDescription>
              </CardHeader>
              <CardContent>
                <form onSubmit={handleAnalyze} className="space-y-4">
