    name: "Trello",
    icon: Trello,
    color: "bg-blue-600",
    fields: [
      { key: "apiKey", label: "Cl√© API", type: "text", placeholder: "Votre cl√© API Trello" },
      { key: "token", label: "Token", type: "password", placeholder: "Votre token Trello" },
