    onError: (error) => {
      if (isUnauthorizedError(error)) {
        toast({
          title: "Non autoris√©",
          description: "Vous √™tes d√©connect√©. Reconnexion en cours...",
          variant: "destructive",
        });
        setTimeout(() => {
          window.location.href = "/api/auth/google";
        }, 500);
        return;
